// import packages
import React, { Component } from 'react'
//import socketIOClient from 'socket.io-client'
import io from 'socket.io-client'
import { v1 } from 'uuid';
const endpoint = "http://localhost:4001" // this is where we are connecting to with sockets
class App extends Component {
    state = {
      //node_id:v1(),
      newReload: true,
      socket:io(endpoint),
      otherNodes_id:[]
    }

  componentWillMount() {
    this.initSocket()
  }

  initSocket = () => {
    //variables
    let node_index = null
    let other_node = null
    let otherNodes_id = [...this.state.otherNodes_id]
    let node_id = null    
    if(!node_index){
      console.log("setting node_id and other nodes: ", node_id , otherNodes_id)
      this.setState({node_id, otherNodes_id})
    }
  } 
  // method for emitting a socket.io event
  send = () => {
    this.state.socket.emit('change color', this.state.color) //'red'
    // socket.emit('change color', 'red', 'yellow') | you can have multiple arguments
  }

  setColor = (color) => {
    this.setState({ color })
  }

  setReload = (e) => {
    console.log("e: ",e)
    if(e==="OK")this.setState({newReload:false});
  }

  pqr = (otherNodes_id) =>{
    this.setState({otherNodes_id})
    //displaySignalMessage('New candidate peer ... trying to connect ' + data.node_id)
    console.log('New candidate peer ... trying to connect ' , otherNodes_id[otherNodes_id.length-1])
  }
  render() {
    const {socket} = this.state

    socket.on('connect', ()=>{      
      console.log("triggered socket.id: ",this.state.socket.id)
    })

    socket.on('newPeer', ({other_node}) => {
      console.log("Entering to register new peer")
      let nodes_id = []
      if(this.state.otherNodes_id) {
        nodes_id = this.state.otherNodes_id;
        let k = 0
        for(let node_id of otherNodes_id){
            if(node_id === other_node){
                node_index = k
            }
            k++
        }
      }
    })

    if(this.state.newReload){
      socket.emit('initiate',
      {"set_room":"SET_ROOM"},this.setReload)
    }

    socket.on('change color', (color) => {
      document.body.style.backgroundColor = color
    })

    return (
      <div style={{ textAlign: "center" }}>
        <button onClick={() => this.send()}>Change Color</button>
        <button id="blue" onClick={() => this.setColor('blue')}>Blue</button>
        <button id="red" onClick={() => this.setColor('red')}>Red</button>
      </div>
    )
  }
}

export default App
