import PouchDB from 'pouchdb'

export default class DB {
    constructor(name){
        this.db = new PouchDB(name)
    }

    getUUID = async () => {
        let node_uuid = await this.db.get('my_id')        
        console.log("node_uuid",node_uuid)
        return node_uuid
    }

    saveUUID = async (my_id) => {
        this.db.put({'my_id':my_id})
    }

}
