import React, {Component} from 'react';
//import { Link } from "../routes.js";
import {Menu,Button,Header,Grid,Input} from "semantic-ui-react";
//import {Link} from "../routes";
import Blockchain from './Blockchain'
import ShowLatestBlock from './ShowLatestBlock'


class CreateBlockChain extends Component {  

  state = {
    blockchain:new Blockchain(),
    data : this.initArray(30)//new Array(30)
  }

   /*getting initial props */
   componentDidMount(){/*

    axios.get('existBlockchain')        
    .then(result =>{
      console.log('El servidor responde que la blockchain (existe/no existe):(true/false)',result);      
      const createdBlockchainInstance=result.data.createdBlockchainInstance;
      this.setState({createdBlockchainInstance});
    }).catch(error =>{
      console.log('error trying to get the blckchain instance status',error);
    });*/
    }

    initArray = (len) => {
        let data = []//new Array(30)
        const value = ''
        for(let i=0;len-1;i++){
            data.push(value) 
        }
        return data
    }


    createBlockchain = () => {
        const newTx = this.state.data;
        const t1 = {
            "amount1":newTx[0],
            "sender1":newTx[1],
            "recipient1":newTx[2],
            "value41":newTx[3],
            "value51":newTx[4],
        }

        const t2 = {
            "amount2":newTx[5],
            "sender2":newTx[6],
            "recipient2":newTx[7],
            "value42":newTx[8],
            "value52":newTx[9],
        }

        const t3 = {
            "amount3":newTx[10],
            "sender3":newTx[11],
            "recipient3":newTx[12],
            "value43":newTx[13],
            "value53":newTx[14],
        }

        const t4 = {
            "amount4":newTx[15],
            "sender4":newTx[16],
            "recipient4":newTx[17],
            "value44":newTx[18],
            "value54":newTx[19],
        }

        const t5 = {
            "amount5":newTx[20],
            "sender5":newTx[21],
            "recipient5":newTx[22],
            "value45":newTx[23],
            "value55":newTx[24],
        }

        const t6 = {
            "amount6":newTx[25],
            "sender6":newTx[26],
            "recipient6":newTx[27],
            "value46":newTx[28],
            "value56":newTx[29]
        }

        const t = [t1,t2,t3,t4,t5,t6]

        const lastBlock = this.state.blockchain.createFirstBlockHandler(t)
        console.log(lastBlock)
        this.setState({lastBlock})
    }

    setData = (order,e) => {
        console.log(this.state.data);
        let data = [...this.state.data];
        const value = e.target.value;
        data[order]=value;
        this.setState({data});
    }

    receiveTxHandler = () => {
        const lastBlock = this.state.lastBlock
        if(!lastBlock){
            return <div style={{ marginTop: "10px" }}>
            <Menu>
            <Menu.Menu position = "left" style={{ marginBottom: "2px" }} >      
            <Button
            content="Create Blockchain"
            icon="add circle"
            labelPosition="left"
            floated="left"
            primary
            onClick={this.createBlockchain}
            disabled = {this.state.createdBlockchainInstance}
            />
            </Menu.Menu>              
            </Menu>

            <Header as='h3' style={ {marginTop: "30px"} } textAlign='center'>Data Sets: </Header>

            <Menu>      
            <Grid columns={3} style={{marginRight:"-80px",marginLeft:"2px",marginTop:"1px", marginBottom:"1px" }} >
            <Grid.Row> <Grid columns={1}><Header as='h5' style={{marginBottom:"-20px",marginLeft:"20px",marginTop:"1px"}} >
            DataSet 1:</Header></Grid> </Grid.Row>

            <Grid.Row style={{marginTop:"2px"}}>          
                <Grid.Column>   
                <Input label ="Tx1-data1" labelPosition='left' value = {this.state.data[0]} placeholder="enter data"
                onChange={e=>this.setData(0,e)} ></Input>
                </Grid.Column>
                <Grid.Column>            
                <Input label ="Tx1-data2" labelPosition='left' value = {this.state.data[1]} placeholder="enter data"
                onChange={e=>this.setData(1,e)} ></Input>
                </Grid.Column>
                <Grid.Column>            
                <Input label ="Tx1-data3" labelPosition='left' value = {this.state.data[2]} placeholder="enter data"
                onChange={e=>this.setData(2,e)} ></Input>
                </Grid.Column>
            </Grid.Row>
            <Grid.Row>          
                <Grid.Column>            
                <Input label ="Tx1-data4" labelPosition='left' value = {this.state.data[3]} placeholder="enter data"
                onChange={e=>this.setData(3,e)} ></Input>
                </Grid.Column>
                <Grid.Column>           
                <Input label ="Tx1-data5" labelPosition='left' value = {this.state.data[4]} placeholder="enter data"
                onChange={e=>this.setData(4,e)} ></Input>
                </Grid.Column>          
            </Grid.Row>            
            </Grid>
            </Menu>


        </div>

        }

    }

    showLatestBlock = () => {
        const lastBlock = this.state.lastBlock
        if(lastBlock){
            return(
            <div>                
                <ShowLatestBlock block={this.state.lastBlock}/>)
            </div>

            )
        }
    }


    render(){
        return(
            <div>
                {this.receiveTxHandler()}
                {this.showLatestBlock()}
            </div>
        )
    }
}

export default CreateBlockChain;
